changeSet.createProcedure:
DROP FUNCTION IF EXISTS mdm_app_version_comparison_index(TEXT);

    CREATE OR REPLACE FUNCTION mdm_app_version_comparison_index(
    version_text TEXT
    )
    RETURNS TEXT AS $$

    DECLARE parts TEXT[];
    DECLARE i INT;
    DECLARE result TEXT;
    DECLARE part TEXT;
    BEGIN
    IF version_text IS NULL THEN
    return -1000000;
    END IF;

    IF LENGTH(TRIM(version_text)) = 0 THEN
    return -1000000;
    END IF;

    result = '';

    parts = STRING_TO_ARRAY(version_text, '.');

    FOR i IN 1 .. ARRAY_UPPER(parts, 1) LOOP
    part = REGEXP_REPLACE(parts[i], '[^0-9]+', '', 'g');

    IF LENGTH(TRIM(part)) = 0 THEN
    part = '0';
    END IF;

    result = result || LPAD(part, 10, '0');
    END LOOP;

    RETURN result;
    END;
    $$
    LANGUAGE 'plpgsql';

changeSet.sql:
-- 1. Copy all applications data to new applicationVersions data and evaluate if respective master data exists DROP TABLE IF EXISTS applicationVersionsTemp; DROP TABLE IF EXISTS applicationFilesToCopyTemp;
SELECT FALSE AS to_be_deleted, FALSE AS to_be_replaced, applications.id AS id, COALESCE( (SELECT apps2.id FROM applications apps2 INNER JOIN customers cust2 ON cust2.id = apps2.customerId WHERE apps2.pkg = applications.pkg AND cust2.master IS TRUE), COALESCE((SELECT id FROM applications ou2 WHERE pkg = applications.pkg AND ou2.customerid=applications.customerid AND (SELECT count(*) FROM applications inr2 WHERE inr2.pkg = ou2.pkg AND ou2.customerid=inr2.customerid AND mdm_app_version_comparison_index(inr2.version) > mdm_app_version_comparison_index(ou2.version)) = 0 ), applications.id)) AS newApplicationId, COALESCE((SELECT apps2.id FROM applications apps2 INNER JOIN customers cust2 ON cust2.id = apps2.customerId WHERE apps2.pkg = applications.pkg AND apps2.version = applications.version AND cust2.master IS TRUE), applications.id) AS newApplicationVersionId, COALESCE((SELECT apps2.url FROM applications apps2 INNER JOIN customers cust2 ON cust2.id = apps2.customerId WHERE apps2.pkg = applications.pkg AND apps2.version = applications.version AND cust2.master IS TRUE), applications.url) AS newUrl, applications.name, applications.pkg, applications.version, applications.url, applications.customerId, customers.master AS isMasterCustomer, COALESCE((SELECT TRUE FROM applications apps2 INNER JOIN customers cust2 ON cust2.id = apps2.customerId WHERE apps2.pkg = applications.pkg AND cust2.master IS TRUE), FALSE) AS masterAppExists, COALESCE((SELECT TRUE FROM applications apps2 INNER JOIN customers cust2 ON cust2.id = apps2.customerId WHERE apps2.pkg = applications.pkg AND apps2.version = applications.version AND cust2.master IS TRUE), FALSE) AS masterVersionExists INTO TABLE applicationVersionsTemp FROM applications INNER JOIN customers ON customers.id = applications.customerId ORDER BY pkg, version, customerid;
-- 2. Mark the application versions which must be deleted since there is exact version found in common applications UPDATE applicationVersionsTemp SET to_be_deleted = TRUE WHERE id <> newApplicationVersionId;
-- 2.0 For multiple same versions of common app UPDATE applicationVersionsTemp ou SET to_be_replaced = TRUE, to_be_deleted = TRUE, newApplicationVersionId = ( SELECT id FROM applicationVersionsTemp ou2 WHERE masterAppExists AND NOT masterVersionExists AND newApplicationId = ou.newApplicationId AND pkg = ou.pkg AND version = ou.version AND (SELECT count(*) FROM applicationVersionsTemp inr2 WHERE inr2.newApplicationId = ou2.newApplicationId AND inr2.pkg = ou2.pkg AND inr2.version = ou2.version AND inr2.id > ou2.id) = 0 ) WHERE masterAppExists AND NOT masterVersionExists AND (SELECT count(*) FROM applicationVersionsTemp inr WHERE inr.newApplicationId = ou.newApplicationId AND inr.pkg = ou.pkg AND inr.version = ou.version AND inr.id > ou.id) > 0;
-- 2.1 Update the newUrls for application versions related to common applications UPDATE applicationVersionsTemp SET newUrl = '${file.resource.url}' || '/ADMIN/' || SUBSTRING(url, LENGTH('${file.resource.url}' || '/') + 1) WHERE masterappexists AND NOT masterversionexists AND NOT url IS NULL AND LEFT(url, LENGTH('${file.resource.url}')) = '${file.resource.url}';
-- 3. Drop referential constraints for tables referencing the applications table ALTER TABLE configurations DROP CONSTRAINT IF EXISTS configurations_mainappid_fkey; ALTER TABLE configurations DROP CONSTRAINT IF EXISTS configurations_contentappid_fkey; -- ALTER TABLE configurationapplications DROP CONSTRAINT IF EXISTS configurationapplications_applicationid_fkey;
-- 3. Update configurations#mainAppId, contentAppId table records to refer to records in applicationVersions according to -- evaluated state UPDATE configurations SET mainAppId = (SELECT app.newApplicationVersionId FROM applicationVersionsTemp app WHERE app.id = configurations.mainappid) WHERE NOT mainAppId IS NULL; UPDATE configurations SET contentAppId = (SELECT app.newApplicationVersionId FROM applicationVersionsTemp app WHERE app.id = configurations.contentAppId) WHERE NOT contentAppId IS NULL;
-- 6. Update configurationApplications#applicationVersionId table records to refer to records in applicationVersions according to -- evaluated state UPDATE configurationApplications SET applicationVersionId = (SELECT app.newApplicationVersionId FROM applicationVersionsTemp app WHERE app.id = configurationApplications.applicationId); UPDATE configurationApplications SET applicationId = (SELECT app.newApplicationId FROM applicationVersionsTemp app WHERE app.id = configurationApplications.applicationId);
-- 7. Delete redundant records from applicationVersions table DELETE FROM applicationVersionsTemp WHERE to_be_deleted IS TRUE;
-- 8. Build the table with list of files which must be copied/moved to Master customer directory SELECT url, ' -> ', newUrl INTO TABLE applicationFilesToCopyTemp FROM applicationVersionsTemp WHERE newUrl <> url;
-- 9. Delete records from applications table which are no longer referenced from the applicationVersions table DELETE FROM applications WHERE NOT EXISTS (SELECT 1 FROM applicationVersionsTemp temp WHERE temp.newApplicationId = applications.id);
-- 10. Copy valid records to applicationVersions table INSERT INTO applicationVersions (id, applicationid, version, url) (SELECT newApplicationVersionId, newApplicationId, version, newUrl FROM applicationVersionsTemp);
-- 11. Update the ID generation sequence ALTER SEQUENCE applicationVersions_id_seq RESTART WITH 10000;
-- 12. Add referential constraints for tables referencing the new applicationVersions table ALTER TABLE configurations ADD CONSTRAINT configurations_mainappid_fkey FOREIGN KEY (mainappid) REFERENCES applicationversions (id) ON DELETE RESTRICT; ALTER TABLE configurations ADD CONSTRAINT configurations_contentappid_fkey FOREIGN KEY (contentappid) REFERENCES applicationversions (id) ON DELETE RESTRICT;
ALTER TABLE configurationApplications ADD CONSTRAINT configurationapplications_applicationversionid_fkey FOREIGN KEY (applicationVersionId) REFERENCES applicationversions (id) ON DELETE RESTRICT;
-- 13. Delete version, url columns from applications table ALTER TABLE applications DROP COLUMN version; ALTER TABLE applications DROP COLUMN url;
-- 14. Set the reference to most recent version for applications records UPDATE applications SET latestVersion = ( SELECT id FROM applicationVersions apv1 WHERE apv1.applicationId = applications.id AND mdm_app_version_comparison_index(apv1.version) = (SELECT MAX(mdm_app_version_comparison_index(apv2.version)) FROM applicationVersions apv2 WHERE apv2.applicationId = applications.id) LIMIT 1 ) WHERE 1=1;
-- 15. Set he name of the application based on the name of the most recent version UPDATE applications SET name = (SELECT name FROM applicationVersionsTemp WHERE applicationVersionsTemp.id = applications.latestVersion);

UPDATE configurationapplications SET showIcon = (SELECT showIcon FROM applications WHERE applications.id = configurationapplications.applicationId);
ALTER TABLE configurationapplications ALTER COLUMN showIcon SET DEFAULT FALSE;
