    deviceId INT NOT NULL REFERENCES devices( id ) ON DELETE CASCADE CONSTRAINT deviceStatuses_pr_key PRIMARY KEY,
    configFilesStatus VARCHAR(100),
    applicationsStatus VARCHAR(100)
);

